define("templates/Dialog",[],function(){return'<div class="dialog-wrap"> <div class="dialog {{type}}"> <a class="dialog-closebtn" href="javascript:"></a> <div class="dialog-head"> <span class="dialog-icon"></span> <span class="title">{{title}}</span> </div> <div class="dialog-content"> {{#content}} </div> <div class="dialog-foot"> <a class="btn btn-ok" href="javascript:">{{okText}}</a> {{if buttons == 2}} <a class="btn btn-cancel" href="javascript:">{{cancelText}}</a> {{/if}} </div> </div> <div class="dialog-mask"></div> </div>'});